def folder = new File( '//var//workspaces//12.3//12.3.1//continuous_elad//xap' )
if( folder.exists() ) {
def cmd = 'cd //var//workspaces//12.3//12.3.1//continuous_elad//xap && git fetch --all -pq  && for branch in `git branch -r | grep -v HEAD`;do echo -e `git show --format="%ci" $branch | head -n 1`  $branch; done | sort -r'
