return lines;
} else{
def cmd = "git ls-remote --heads https://github.com/xap/xap.git | awk ' { gsub(/refs\\/heads\\//, \"\"); print \$2 }' "
proc1 = ['/bin/bash', '-c',cmd].execute()
return proc1.text.split("\n").toList()
}

